list-view-config.query-select:
PREFIX afn:   <http://jena.hpl.hp.com/ARQ/function#>
PREFIX core:  <http://vivoweb.org/ontology/core#>
PREFIX rdfs:  <http://www.w3.org/2000/01/rdf-schema#>
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
PREFIX bprofile: <http://vivo.brown.edu/ontology/profile#>

SELECT DISTINCT ?subclass
                ?happt
                ?happtText
                ?emp ?empText
                ?spec ?specText
                ?start ?end ?hdept
WHERE {
    ?subject ?property ?happt .
    ?happt rdfs:label ?happtText .
    OPTIONAL {

                ?happt vitro:mostSpecificType ?subclass

        }
    OPTIONAL { ?happt bprofile:hasHospitalAppointmentOrganization ?emp .
                ?emp rdfs:label ?empText }
    OPTIONAL { ?happt bprofile:startDate ?start }
    OPTIONAL { ?happt bprofile:endDate ?end }
    OPTIONAL { ?happt bprofile:department ?hdept }
}
ORDER BY ?subclass DESC(?start)

list-view-config.query-construct:
PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
PREFIX bprofile: <http://vivo.brown.edu/ontology/profile#>

CONSTRUCT {
    ?subject ?property ?happt .
    ?happt rdfs:label ?happtText .
    ?happt vitro:mostSpecificType ?subclass .
    ?happt bprofile:hasHospitalAppointmentOrganization ?emp .
    ?emp rdfs:label ?empText .
    ?happt bprofile:startDate ?start .
    ?happt bprofile:endDate ?end .
    ?happt bprofile:department ?hdept .
} WHERE {
    {
        ?subject ?property ?happt .
        ?happt rdfs:label ?happtText .
    }
    UNION {
        ?subject ?property ?happt .
        ?happt vitro:mostSpecificType ?subclass .   
    }
    UNION {
        ?subject ?property ?happt .
        ?happt bprofile:hasHospitalAppointmentOrganization ?emp .
        ?emp rdfs:label ?empText .         
    }
    UNION {
        ?subject ?property ?happt .
        ?happt bprofile:startDate ?startDate .
        BIND(year(?startDate) as ?start) .            
    }
    UNION {
        ?subject ?property ?happt .
        ?happt bprofile:endDate ?endDate .
        BIND((IF
            (year(?endDate) < year(NOW()), year(?endDate), "Present")
            )
            as ?end) .            
    }
    UNION {
        ?subject ?property ?happt .
        ?happt bprofile:department ?hdept .           
    }
}
