list-view-config.query-select:
PREFIX afn:   <http://jena.hpl.hp.com/ARQ/function#>
PREFIX core:  <http://vivoweb.org/ontology/core#>
PREFIX rdfs:  <http://www.w3.org/2000/01/rdf-schema#>
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
PREFIX bprofile: <http://vivo.brown.edu/ontology/profile#>

SELECT DISTINCT ?subclass
                ?pdoc
                ?pdocText
                ?org ?orgText
                ?hosp ?hospText
                ?spec ?specText
                ?start ?end
                ?pdocCity ?pdocState ?pdocCountry
WHERE {
    ?subject ?property ?pdoc .
    ?pdoc rdfs:label ?pdocText .
    OPTIONAL {

                ?pdoc vitro:mostSpecificType ?subclass

        }
    OPTIONAL { ?pdoc bprofile:hasPostdocAppointmentOrganization ?org .
                ?org rdfs:label ?orgText }
    OPTIONAL { ?pdoc bprofile:hasPostdocAppointmentHospital ?hosp .
                ?hosp rdfs:label ?hospText }
    OPTIONAL { ?pdoc bprofile:hasPostdocSpecialtyOrDepartment ?spec .
                ?spec rdfs:label ?specText }
    OPTIONAL { ?pdoc bprofile:startDate ?start }
    OPTIONAL { ?pdoc bprofile:endDate ?end }
    OPTIONAL { ?pdoc bprofile:city ?pdocCity }
    OPTIONAL { ?pdoc bprofile:state ?pdocState }
    OPTIONAL { ?pdoc bprofile:country ?pdocCountry }
}
ORDER BY ?subclass DESC(?start)

list-view-config.query-construct:
PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
PREFIX bprofile: <http://vivo.brown.edu/ontology/profile#>

CONSTRUCT {
    ?subject ?property ?pdoc .
    ?pdoc rdfs:label ?pdocText .
    ?pdoc vitro:mostSpecificType ?subclass .
    ?pdoc bprofile:hasPostdocAppointmentOrganization ?org .
    ?org rdfs:label ?orgText .
    ?pdoc bprofile:hasPostdocAppointmentHospital ?hosp .
    ?hosp rdfs:label ?hospText .
    ?pdoc bprofile:hasPostdocSpecialtyOrDepartment ?spec .
    ?spec rdfs:label ?specText .
    ?pdoc bprofile:startDate ?start .
    ?pdoc bprofile:endDate ?end .
    ?pdoc bprofile:city ?pdocCity .
    ?pdoc bprofile:state ?pdocState .
    ?pdoc bprofile:country ?pdocCountry .
} WHERE {
    {
        ?subject ?property ?pdoc .
        ?pdoc rdfs:label ?pdocText .
    }
    UNION {
        ?subject ?property ?pdoc .
        ?pdoc vitro:mostSpecificType ?subclass .   
    }
    UNION {
        ?subject ?property ?pdoc .
        ?pdoc bprofile:hasPostdocAppointmentOrganization ?org .
        ?org rdfs:label ?orgText .         
    }
    UNION {
        ?subject ?property ?pdoc .
        ?pdoc bprofile:hasPostdocAppointmentHospital ?hosp .
        ?hosp rdfs:label ?hospText .         
    }
    UNION {
        ?subject ?property ?pdoc .
        ?pdoc bprofile:hasPostdocSpecialtyOrDepartment ?spec .
        ?spec rdfs:label ?specText .         
    }
    UNION {
        ?subject ?property ?pdoc .
        ?pdoc bprofile:startDate ?startDate .
        BIND(year(?startDate) as ?start) .            
    }
    UNION {
        ?subject ?property ?pdoc .
        ?pdoc bprofile:endDate ?endDate .
        BIND((IF
            (year(?endDate) < year(NOW()), year(?endDate), "Present")
            )
            as ?end) .           
    }
    UNION {
        ?subject ?property ?pdoc .
        ?pdoc bprofile:city ?pdocCity .           
    }
    UNION {
        ?subject ?property ?pdoc .
        ?pdoc bprofile:state ?pdocState .           
    }
    UNION {
        ?subject ?property ?pdoc .
        ?pdoc bprofile:country ?pdocCountry .           
    }
}
